// Game JS$(document).ready(function() {	      /* Download open in footer */	$(".openerFT").click(function() {		$(".subFT").toggle();	});		/* Newsletter Sign Up */	$("#logonEmailId").click(function() {		$(this).removeAttr('placeholder');	});		// Preownd button Hacks	$("body#pgProductDetails #aside #variants .priceItem.topsellers ul.product-descriptions li.product-description .preowned-guarantee-tip").each(function() {        $(this).parent().addClass("gameproduct");    });	$("body#pgProductDetails #aside #variants li.priceItem ul.preowned li.available").each(function() {		$(this).parent().parent().hide();	});		$('body#pgArticle.article.art #secondary #relatedProducts #productLister ul.products div.imageHolder div.photo > a > img').each(function() {        var imgAtt = $(this).attr('data-src');		$(this).attr('src', imgAtt);    });		$('body.hub1050751 ul.fullWidthContainer li#collectors.innerSection.splitSection > div > div.info ul.buySection a:last-of-type li').html('3');		/*$('body.hub969296 .offerBox a').html('Watch the Pokemon Direct here from 3 p.m. (UK)!');*/});$(window).ready(function() {	setTimeout(function(){		// if not preowned game stock hide preowned button		var itemsToBuy = $("body#pgProductDetails #aside #variants li.priceItem.topsellers ul.product-descriptions > li").length;				if(itemsToBuy <= 1 && $("body#pgProductDetails #aside #variants li.priceItem.topsellers ul.product-descriptions > li").hasClass("gameproduct")) {			$("body#pgProductDetails #aside #variants li.priceItem.topsellers").hide();		}		/*		var hasGAME = $("#pgProductDetails #marketplaceContent li.item.game").length;				if (hasGAME < 1) {			$("#pgProductDetails #aside .clickAndCollectAvailable").hide();		}		*/	}, 2000);});